<template>            <canvas canvas-id="canvasColumnMeter" id="canvasColumnMeter" class="charts" ></canvas></template><script>    import uCharts from '@/u-charts/u-charts.js';    var _self;    var canvaColumn=null;    export default {        data() {            return {                cWidth:'',                cHeight:'',                pixelRatio:1,                serverData:'',            }        },        mounted() {            _self = this;            this.cWidth=uni.upx2px(750);            this.cHeight=uni.upx2px(450);            this.getServerData();        },        methods: {            getServerData(){				uni.request({					url: this.websiteUrl+'/weather/precp7d',					data:{						city:uni.getStorageSync('list')[uni.getStorageSync('cityIndex')].city,					},					success: (res)=> {						let ColumnMeter={categories:[],series:[]};						ColumnMeter.series=[{										"name": "降雨量",										"data": [],										"color": "#409eff",										textColor:'#409eff',									  }]						var maxArray=new Array();						var minArray=new Array();					    for(var i=0;i<7;i++)						{							maxArray[i]=res.data.data[i].precp;							minArray[i]=res.data.data[i].precp;							ColumnMeter.categories[i]=res.data.data[i].day.substring(5);							ColumnMeter.series[0].data[i]=res.data.data[i].precp;						}						var max=maxArray[0];						var min=maxArray[0];						for(var i=0;i<7;i++)						{						if(max<maxArray[i]){max=maxArray[i];}						if(min>minArray[i]){min=minArray[i];}						}//找出最大的那个温度数据						_self.showColumnMeter("canvasColumnMeter",ColumnMeter,max,min);									},					// 请求成功的回调函数				});	                    },            showColumnMeter(canvasId,chartData,maxData,minData){                canvaColumn=new uCharts({                    $this:_self,                    canvasId: canvasId,                    type: 'column',                    legend:true,                    fontSize:13,					legend:{show:false},                    pixelRatio:_self.pixelRatio,                    animation: true,                    categories: chartData.categories,                    series: chartData.series,                    xAxis: {                        disableGrid:true,						fontColor:'white',                    },                    yAxis: {						    disabled:true,						    disableGrid:true,							max:parseInt(maxData)+1,//为了保证最大的数据能够完成的在图标里面显示出来							min:parseInt(minData),//为了保证最小的数据能够完成的在图标里面显示出来                    },                    dataLabel: true,                    width: _self.cWidth*_self.pixelRatio,                    height: _self.cHeight*_self.pixelRatio,                    extra: {                        column: {                            type:'meter',                            width: _self.cWidth*_self.pixelRatio*0.45/chartData.categories.length,                            meter:{                                //这个是外层边框的宽度                                border:1,                                //这个是内部填充颜色                                fillColor:'#409eff'                            }                        }                      }                });            },        }    }</script><style>.charts{width: 100%; height:500upx;}</style>